--DECLARE @ParamDateStart DATE = '2024-02-11'; --DECLARE @ParamDateEnd DATE = '2024-02-25'; --DECLARE @ParamBranchId INT = 42
SELECT EmployeeDtr.EmployeeId, EmployeeDtr.BranchId, EmployeeDtr.CompanyId, EmployeeDtr.FullName, EmployeeDtr.BiometricIdNumber, IIF(@ParamBranchId = 0, 'All', MstBranch.Branch) as Branch, IIF(@ParamCompanyId = 0, 'All', MstCompany.Company) as Company, ISNULL(EmployeePresentCount.NoOfDaysPresent, 0) AS NoOfDaysPresent1, ISNULL(EmployeeTardyCount.NoOfDaysTardy, 0) AS NoOfDaysTardy1, ISNULL(EmployeePresentCount.NoOfDaysPresent, 0) - ISNULL(EmployeeTardyCount.NoOfDaysTardy, 0) AS DaysWithAllowance, ISNULL(EmployeeDtr.AllowanceRate, 0) AS AllowanceRate1, (ISNULL(EmployeePresentCount.NoOfDaysPresent, 0) - ISNULL(EmployeeTardyCount.NoOfDaysTardy, 0)) * ISNULL(EmployeeDtr.AllowanceRate, 0) AS AllowanceAmount FROM ( SELECT TrnDTRLine.EmployeeId, MstEmployee.FullName, MstEmployee.BranchId, MstEmployee.CompanyId, ISNULL(MstEmployee.Allowance, 0) AS AllowanceRate, MstEmployee.BiometricIdNumber FROM TrnDTRLine LEFT JOIN MstEmployee ON TrnDTRLine.EmployeeId = MstEmployee.Id WHERE TrnDTRLine.Date BETWEEN @ParamDateStart AND @ParamDateEnd GROUP BY TrnDTRLine.EmployeeId, MstEmployee.FullName, MstEmployee.BranchId, MstEmployee.CompanyId, ISNULL(MstEmployee.Allowance, 0), MstEmployee.BiometricIdNumber ) EmployeeDtr LEFT JOIN ( SELECT TrnDTRLine.EmployeeId, COUNT(DISTINCT TrnDTRLine.Date) AS NoOfDaysTardy FROM TrnDTRLine WHERE TrnDTRLine.TardyAmount > 0 AND TrnDTRLine.Date BETWEEN @ParamDateStart AND @ParamDateEnd GROUP BY TrnDTRLine.EmployeeId ) EmployeeTardyCount ON EmployeeDtr.EmployeeId = EmployeeTardyCount.EmployeeId LEFT JOIN ( SELECT TrnDTRLine.EmployeeId, COUNT(DISTINCT TrnDTRLine.Date) AS NoOfDaysPresent FROM TrnDTRLine WHERE (TrnDTRLine.Absent = 0 AND TrnDTRLine.HalfdayAbsent = 0) AND (ISNULL(TrnDTRLine.TimeIn1, 0) <> 0 OR ISNULL(TrnDTRLine.TimeOut1, 0) <> 0 OR ISNULL(TrnDTRLine.TimeIn2, 0) <> 0 OR ISNULL(TrnDTRLine.TimeOut2, 0) <> 0) AND TrnDTRLine.Date BETWEEN @ParamDateStart AND @ParamDateEnd GROUP BY TrnDTRLine.EmployeeId ) EmployeePresentCount ON EmployeeDtr.EmployeeId = EmployeePresentCount.EmployeeId LEFT JOIN dbo.MstBranch ON EmployeeDtr.BranchId = dbo.MstBranch.Id LEFT JOIN dbo.MstCompany ON EmployeeDtr.CompanyId = dbo.MstCompany.Id WHERE (@ParamBranchId = 0 OR EmployeeDtr.BranchId = @ParamBranchId) AND (@ParamCompanyId = 0 OR EmployeeDtr.CompanyId = @ParamCompanyId) AND (ISNULL(EmployeePresentCount.NoOfDaysPresent, 0) - ISNULL(EmployeeTardyCount.NoOfDaysTardy, 0)) * ISNULL(EmployeeDtr.AllowanceRate, 0) > 0 ORDER BY EmployeeDtr.FullName;
